import React, { Component } from 'react';
import './comments.css';

class Comments extends Component {
    state={
        list:[]
    }
    render() { 
        return (  
            <div className="comments-div">
                <h3 className="Comments-heading">Comments</h3>
                <form onSubmit={this.append-list.bind(this)}>
                <input type="text" placeholder="Your Name" className="comments-name" required></input>
                <input type="text" placeholder="Your Comment" className="add-comments" required></input>
                <button className="btn btn-primary" type="submit">Comment</button>
                </form>
                <div>
                    <ul className="list-to-be-added"></ul>
                </div>
            </div>
        );
    }
    
}
 
export default Comments;
